mapper.select:
SELECT

FROM demo_account WHERE 1=1

	AND name = #{name}

		SELECT
			count(id) as count,
			IFNULL(sum(balance),0) as sum_balance,
			IFNULL(sum(credit_line),0) as sum_credit_line,
			IFNULL(max(age),0) as max_age
		FROM
		demo_account
		
			
				AND name = #{name}
			
			
				AND card_type = #{card_type}
			
			
				AND card_id = #{card_id}
